function factorial(n) {
    function iter(product, counter) {
        return counter > n
               ? product
               : iter(counter * product,
                      counter + 1);
    }

    return iter(1, 1);
}

function fib(n) {
    return n < 2 ? n : fib(n - 1) + fib(n - 2);
}
